import {useState} from "react";
import User from './pages/User'

function App() { //Componente como uma função que retorana um html

  const [counter, setCounter] = useState(0);
  function increment(){
    setCounter(counter + 1);
  }

  // let counter = 0; //Com variável não fuciona pois o react é imutável 
  // function increment(){
  //   counter++;
  //   console.log(counter);
  // }

  return (
    <div className="App">
      <div>
        Contador:{counter}
      </div>
      <button onClick={increment}>Incrementar</button>
    </div>
  );
}

export default App;
